mapper.select:
select * from en_company

	del_flag = 0

		and `create_user` = #{createUser}


		and `company_name` like concat('%',#{companyName},'%')




		order by ${sidx} ${order}


		order by create_time desc



	limit #{offset}, #{limit}

select * from en_company

	del_flag = 0
	and company_name = #{enCompany.companyName}
	and id <> #{enCompany.id}

mapper.update:
update en_company
company_name = #{companyName}, company_shortname = #{companyShortname}, company_ename = #{companyEname}, pinyin_index = #{pinyinIndex}, company_areapath = #{companyAreapath}, company_address = #{companyAddress}, company_fr = #{companyFr}, company_fr_phone = #{companyFrPhone}, company_contact = #{companyContact}, contact_phone = #{contactPhone}, company_key = #{companyKey}, company_logo = #{companyLogo}, company_type = #{companyType}, company_capacity = #{companyCapacity}, company_lo = #{companyLo}, company_la = #{companyLa}, pay_type = #{payType}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

update en_company
company_key = #{enCompany.companyKey}, key_type = #{enCompany.keyType}, key_sttime = #{enCompany.keySttime}, key_edtime = #{enCompany.keyEdtime}, update_time = #{enCompany.updateTime}, update_user = #{enCompany.updateUser}
where id = #{enCompany.id}
